            h = (h - 1 + MOD) % MOD;
    }

    map<ll, int> mp;

    ll ans = 0;

    mp[0]++;

    a[n] = 1;

    for (int i = n - 1; i >= 0; i--) {
        a[i] = a[i + 1];
        b[i] = b[i + 1];

        if (s[i] == '>') {
            a[i] = a[i + 1] * inv_X % MOD;
            b[i] = b[i + 1];
        }
        else if (s[i] == '<') {
            a[i] = a[i + 1] * X % MOD;
            b[i] = b[i + 1];
        }
        else if (s[i] == '+') {
            a[i] = a[i + 1];
            b[i] = (b[i] - a[i + 1] + MOD) % MOD;
        }
        else {
            a[i] = a[i + 1];
            b[i] = (b[i] + a[i + 1]) % MOD;
        }

        // cout << b[i] << "\n";

        ans += mp[(a[i] * h + b[i]) % MOD];
        mp[b[i]]++;
    }

    // cout << (a[0] * h + b[0]) % MOD << "\n";

    cout << ans << "\n";

    return 0;   
}

