ll inv(int x){return (x==1 || x==-1)?x:(-(MOD/x)*inv(MOD%x)%MOD);}

int t0[N*2],*pow;

void initialize()
{
	pow=t0+N;

	pow[0]=1;
	for(int i=1;i<N;i++)
		pow[i]=(ll)pow[i-1]*g%MOD;

	int ig=(inv(g)+MOD)%MOD;
	for(int i=0;i<N;i++)
		pow[-i-1]=(ll)pow[-i]*ig%MOD;
}

char s[N];
