int n;

ll ans;

void prework()
{
	int p=0;
	for(int i=1;i<=n;i++)
	{
		ind[i]=p,val[i]=0;

		if(s[i]=='<')p--;
		else if(s[i]=='>')p++;
		else if(s[i]=='+')val[i]=pow[p];
		else val[i]=-pow[p];
	}

	for(int i=1;i<=n;i++)
		sum[i]=((sum[i-1]+val[i])%MOD+MOD)%MOD;
}

std::map<int,int> G;

void solve()
{
	ans=0;
	for(int i=n,x,y;i;i--)
	{
		G[sum[i]]++;

		x=((ll)sum[n]*pow[ind[i]]%MOD+MOD)%MOD;
		y=((x+sum[i-1])%MOD+MOD)%MOD;

		ans+=G[y];
	}
}

