typedef long long lint;

lint modpow(lint x, lint y, lint mod) {
  lint ret = 1 % mod, x2p = x;
  while (y > 0) {
    if (y % 2) ret = (__int128)ret * x2p % mod;
    y /= 2;
    x2p = (__int128)x2p * x2p % mod;
  }
  return ret;
}

const lint mod_1 = 1152921504609811141LL, mod_2 = 576460752312103501LL;
const lint base_1 = 102999959LL, base_2 = 1006199503LL;
const lint inv_base_1 = modpow(base_1, mod_1 - 2, mod_1);
const lint inv_base_2 = modpow(base_2, mod_2 - 2, mod_2);

int main() {
  int n;
  scanf("%d", &n);
  string seq(n, ' ');
  for (int i = 0; i < n; ++i)
    scanf(" %c", &seq[i]);

  vector<int> pos_before(n);

  //
  vector<lint> base_pow_1(n + 1);
  base_pow_1[0] = 1;
  for (int i = 1; i <= n; ++i)
    base_pow_1[i] = (__int128)base_1 * base_pow_1[i - 1] % mod_1;

  vector<lint> inv_base_pow_1(n + 1);
  inv_base_pow_1[0] = 1;
  for (int i = 1; i <= n; ++i)
    inv_base_pow_1[i] = (__int128)inv_base_1 * inv_base_pow_1[i - 1] % mod_1;

  vector<lint> hash_diff_1(n, 0);
  int pos = 0;
  for (int i = 0; i < n; ++i) {
    pos_before[i] = pos;
    if (seq[i] == '>') ++pos;
    else if (seq[i] == '<') --pos;
    else {
      lint base_pow_1_pos;
      if (pos >= 0) base_pow_1_pos = base_pow_1[pos];
      else base_pow_1_pos = inv_base_pow_1[-pos];
      if (seq[i] == '+') hash_diff_1[i] = base_pow_1_pos;
      else if (seq[i] == '-') hash_diff_1[i] = mod_1 - base_pow_1_pos;
      else assert(0);
    }
  }
  lint full_seq_hash_1 = 0;
  for (int i = 0; i < n; ++i) {
    full_seq_hash_1 += hash_diff_1[i];
    if (full_seq_hash_1 >= mod_1) full_seq_hash_1 -= mod_1;
  }

  vector<lint> suffix_1(n + 1);
  suffix_1[n] = 0;
  for (int i = n - 1; i >= 0; --i) {
    suffix_1[i] = hash_diff_1[i] + suffix_1[i + 1];
    if (suffix_1[i] >= mod_1) suffix_1[i] -= mod_1;
  }

  //
  vector<lint> base_pow_2(n + 1);
  base_pow_2[0] = 1;
  for (int i = 1; i <= n; ++i)
    base_pow_2[i] = (__int128)base_2 * base_pow_2[i - 1] % mod_2;

  vector<lint> inv_base_pow_2(n + 1);
  inv_base_pow_2[0] = 1;
  for (int i = 1; i <= n; ++i)
    inv_base_pow_2[i] = (__int128)inv_base_2 * inv_base_pow_2[i - 1] % mod_2;

  vector<lint> hash_diff_2(n, 0);
  pos = 0;
  for (int i = 0; i < n; ++i) {
    if (seq[i] == '>') ++pos;
    else if (seq[i] == '<') --pos;
    else {
      lint base_pow_2_pos;
      if (pos >= 0) base_pow_2_pos = base_pow_2[pos];
      else base_pow_2_pos = inv_base_pow_2[-pos];
      if (seq[i] == '+') hash_diff_2[i] = base_pow_2_pos;
      else if (seq[i] == '-') hash_diff_2[i] = mod_2 - base_pow_2_pos;
      else assert(0);
    }
  }
  lint full_seq_hash_2 = 0;
  for (int i = 0; i < n; ++i) {
    full_seq_hash_2 += hash_diff_2[i];
    if (full_seq_hash_2 >= mod_2) full_seq_hash_2 -= mod_2;
  }

  vector<lint> suffix_2(n + 1);
  suffix_2[n] = 0;
  for (int i = n - 1; i >= 0; --i) {
    suffix_2[i] = hash_diff_2[i] + suffix_2[i + 1];
    if (suffix_2[i] >= mod_2) suffix_2[i] -= mod_2;
  }
  //

  lint result = 0;
  map<pair<lint, lint>, int> count;
  count[{suffix_1[n], suffix_2[n]}] += 1;
  for (int i = n - 1; i >= 0; --i) {
    lint shifted_full_1, shifted_full_2;
    if (pos_before[i] >= 0) {
      shifted_full_1 = (__int128)full_seq_hash_1 * base_pow_1[pos_before[i]] % mod_1;
      shifted_full_2 = (__int128)full_seq_hash_2 * base_pow_2[pos_before[i]] % mod_2;
    } else {
      shifted_full_1 = (__int128)full_seq_hash_1 * inv_base_pow_1[-pos_before[i]] % mod_1;
      shifted_full_2 = (__int128)full_seq_hash_2 * inv_base_pow_2[-pos_before[i]] % mod_2;
    }

    lint target_1 = suffix_1[i] - shifted_full_1;
    if (target_1 < 0) target_1 += mod_1;

    lint target_2 = suffix_2[i] - shifted_full_2;
    if (target_2 < 0) target_2 += mod_2;

    target_1 = (target_1 % mod_1 + mod_1) % mod_1;
    target_2 = (target_2 % mod_2 + mod_2) % mod_2;

    auto it = count.find({target_1, target_2});
    if (it != count.end())
      result += it->second;

    suffix_1[i] = (suffix_1[i] % mod_1 + mod_1) % mod_1;
    suffix_2[i] = (suffix_2[i] % mod_2 + mod_2) % mod_2;

    count[{suffix_1[i], suffix_2[i]}] += 1;
  }
  printf("%lld\n", result);

  return 0;
}

