const int mod = 998244353;
const int maxN = 500000 + 40;
const int pt[2] = {228, 1337};
int pw[2][maxN];
int sum(int a, int b) {
    int s = a + b;
    if (s >= mod) s -= mod;
    return s;
}
int mult(int a, int b) {
    return (1LL * a * b) % mod;
}
int sub(int a, int b) {
    int s = a - b;
    if (s < 0) s += mod;
    return s;
}
int pww(int a, int b) {
    if (b == 0) return 1;
    if (b & 1) return mult(a, pww(a, b - 1));
    int res = pww(a, b / 2);
    return mult(res, res);
}
int n;
int hsh[2][maxN];
char c[maxN];
int pos[maxN];
int inv[2][maxN];
int main() {
    //freopen("input.txt", "r", stdin);
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    cin >> n;
    for (int i = 0; i < 2; i++) {
        pw[i][0] = 1;
        inv[i][0] = 1;
        int inv_val = pww(pt[i], mod - 2);
        for (int j = 1; j < maxN; j++) {
            pw[i][j] = mult(pw[i][j - 1], pt[i]);
            inv[i][j] = mult(inv[i][j - 1], inv_val);
        }
    }
    const int SHIFT = maxN / 2;
    int CUR = 0;
    for (int i = 1; i <= n; i++) {
        cin >> c[i];
        for (int j = 0; j < 2; j++) {
            hsh[j][i] = hsh[j][i - 1];
        }
        if (c[i] == '>') CUR++;
        else if (c[i] == '<') CUR--;
        else if (c[i] == '+') {
            for (int j = 0; j < 2; j++) {
                hsh[j][i] = sum(hsh[j][i], pw[j][CUR + SHIFT]);
            }
        }
        else if (c[i] == '-') {
            for (int j = 0; j < 2; j++) {
                hsh[j][i] = sub(hsh[j][i], pw[j][CUR + SHIFT]);
            }
        }
        else {
            assert(false);
        }
        pos[i] = CUR;
    }
    //diff
    map < pair < int, int >, int > mp;
    ll ans = 0;
    for (int suf = n; suf >= 1; suf--) {
        mp[make_pair(hsh[0][suf], hsh[1][suf])]++;
        int t[2] = {0, 0};
        for (int j = 0; j < 2; j++) {
            if (pos[suf - 1] >= 0) {
                t[j] = sum(hsh[j][suf - 1], mult(hsh[j][n], pw[j][pos[suf - 1]]));
            }
            else {
                t[j] = sum(hsh[j][suf - 1], mult(hsh[j][n], inv[j][-pos[suf - 1]]));
            }
        }
        ans += mp[make_pair(t[0], t[1])];
    }
    cout << ans;

    return 0;
}

