using namespace std;

const int Mod = 1e9 + 7, Nmax = 5e5 + 5, Pow = 31;
typedef long long ll;

int sP[Nmax], i, n, P, target;
ll Pw[Nmax], E[Nmax], R[Nmax], ans = 0, Inv, InvP[Nmax];
map<int, int> mp;
char a[Nmax];


ll power(ll a, int b)
{
    ll ans = 1;
    while(b)
    {
        if(b&1) ans = ans * a % Mod;
        b >>= 1; a = a*a%Mod;
    }
    return ans;
}

int main()
{
  //  freopen("input", "r", stdin);
 //   freopen("output", "w", stdout);
    cin.sync_with_stdio(false);

    cin >> n >> (a+1);

    for(i=1; i<=n; ++i) sP[i] = sP[i-1] - (a[i] == '<') + (a[i] == '>');

    Inv = power(Pow, Mod-2);
    Pw[0] = 1; InvP[0] = 1;
    for(i=1; i<=2*n; ++i) Pw[i] = Pw[i-1] * Pow % Mod, InvP[i] = InvP[i-1] * Inv % Mod;

    P = n;
    for(i=1; i<=n; ++i) /// for 1..,i
    {
        E[i] = E[i-1];
        if(a[i] == '<')
        {
            --P;
            continue;
        }

        if(a[i] == '>')
        {
            ++P;
            continue;
        }

        if(a[i] == '-')
            E[i] -= Pw[P];
        else E[i] += Pw[P];

        if(E[i] >= Mod) E[i] -= Mod;
            else if(E[i] < 0) E[i] += Mod;
    }

    for(i=1; i<=n; ++i) /// reverse operations for 1...i, not signs
    {
        if(a[i] == '<')
        {
            R[i] = Pow * R[i-1] % Mod;
            continue;
        }

        if(a[i] == '>')
        {
            R[i] = Inv * R[i-1] % Mod;
            continue;
        }

        if(a[i] == '+') R[i] = R[i-1] + Pw[n];
            else R[i] = R[i-1] - Pw[n];

        if(R[i] >= Mod) R[i] -= Mod;
            else if(R[i] < 0) R[i] += Mod;
    }

    for(i=n; i; --i)
    {
        ++mp[E[i]]; /// i becomes active

        target = E[n] + R[i-1];
        if(sP[i-1] >= 0) target = (ll) target * Pw[sP[i-1]] % Mod;
            else target = (ll) target * InvP[-sP[i-1]] % Mod;

        ans += mp[target];
    }

    cout << ans << '\n';
    return 0;
}

