		if(s[i]=='<')g[i]=g[i]*inv;
		if(s[i]=='>')g[i]=g[i]*b;
		mp[f[i]]++;
	}
	D ans=0;
	for(int i=1;i<=n;i++){
		ans+=mp[f[i-1]+g[i-1]*f[n]];
		mp[f[i]]--;
	}
	printf("%lld\n",ans);
