    par.assign(d,vi(n)); adj.resz(n); depth.resz(n);
}
void ae(int x, int y) { adj[x].pb(y), adj[y].pb(x); }
void dfs(int x = 0) {
    FOR(i,1,sz(par)) par[i][x] = par[i-1][par[i-1][x]];
    trav(y,adj[x]) if (y != par[0][x])
        depth[y] = depth[par[0][y] = x]+1, dfs(y);
}
void gen(int R = 0) { par[0][R] = R; dfs(R); }

int jmp(int x, int d) {
    F0R(i,sz(par)) if ((d>>i)&1) x = par[i][x];
    return x; }
int lca(int x, int y) {
    if (depth[x] < depth[y]) swap(x,y);
    x = jmp(x,depth[x]-depth[y]); if (x == y) return x;
    R0F(i,sz(par)) {
        int X = par[i][x], Y = par[i][y];
        if (X != Y) x = X, y = Y;
    }
    return par[0][x];
}
int dist(int x, int y) { // # edges on path
    return depth[x]+depth[y]-2*depth[lca(x,y)]; }
/// edges numbered 1 to n-1 inclusive
bool vis[MAX];
vi edges;
int ans = 1;
void dfs_vert(int src){
    if(sz(adj[src])%2 == 1) return;
    for(int nxt: adj[src]){
        /// src to nxt
        int val = (depth[src]<depth[nxt] ? nxt:src);
        if(vis[val]) continue;
        vis[val] = true;
        edges.eb(val);
        dfs_vert(nxt);
    }
}
int dep[MAX];
bool ok;
bool check_even(vi& v, int rid, int mx){
    int l = 0;
    int r = sz(v) - 1;
    while(l<=r){
        if(l == rid) l++;
        if(r == rid) r--;
        if(l>r) return true;
        if(v[l] + v[r]>mx) return false;
        l++; r--;
    }
    return true;
}
int solve_even(vi & legs, int mx){
    int lo = 0;
    int hi = sz(legs) - 1;
    while(hi-lo>1){
        int mid = (lo+hi)/2;
        if(check_even(legs, mid, mx)) hi = mid;
        else lo = mid+1;
    }
    if(check_even(legs, lo, mx)) return legs[lo];
    else if(check_even(legs, hi, mx)) return hi;
    else{
        ok = false;
        return legs.back();
    }
}
int solve_odd(vi & legs, int mx){
    int l = 0;
    int r = sz(legs) - 1;
    bool good = true;
    while(l<=r){
        if(legs[l]+legs[r]>mx) {
            good = false; break;
        }
        l++; r--;
    }
    if(good) return 0;
    int id = -1;
    f0r(i, sz(legs)) if(legs[i]<mx) id = i;
    if(id == -1){
        ok = false;
        return legs.back();
    }
    legs.erase(legs.begin()+id);
    return solve_even(legs, mx);
}
int dfs_solve(int src, int par, int mx){
    if(sz(adj[src]) == 1) return 0;
    vi legs;
    for(int nxt: adj[src]){
        if(nxt == par) continue;
        legs.eb(dfs_solve(nxt, src, mx)+1);
    }
    sort(all(legs));
    if(legs.back()>mx){
        ok = false;
        return legs.back();
    }
    if(src == 0){
        if(sz(adj[src])%2 == 0){
            int l = 0;
            int r = sz(legs)-1;
            while(l<=r){
                if(legs[l]+legs[r]>mx){
                    ok = false; break;
                }
                l++; r--;
            }
        }
        else{
            if(sz(adj[src]) == 1){
                if(legs.back()>mx) ok = false;
                return legs.back();
            }
            int id = -1;
            f0r(i, sz(legs)) if(legs[i]<mx) id = i;
            if(id == -1){
                ok = false;
                return legs.back();
            }
            legs.erase(legs.begin()+id);
            int l = 0; int r = sz(legs)-1;
            while(l<=r){
                if(legs[l]+legs[r]>mx) {
                    ok = false; break;
                }
                l++; r--;
            }
        }
        return legs.back();

    }
    if(sz(adj[src])%2 == 0) return solve_even(legs, mx);
    else return solve_odd(legs, mx);
}
int main(){
    setIO("");
    re(n);
    init(n);
    f0r(i, n-1){
        int u, v; re(u, v); u--; v--;
        ae(u, v);
    }
    gen();
    int ed = 0;
    f0r(i, n) if(sz(adj[i])%2 == 1) ed++;
    int a = ed/2;
    int lo = 1;
    int hi = MAX;
    while(hi-lo>1){
        int mid = (lo+hi)/2;
        ok = true;
        dfs_solve(0, -1, mid);
        if(ok) hi = mid;
        else lo = mid+1;
    }
    ok = true;
    dfs_solve(0, -1, lo);
    if(ok) ckmax(ans, lo);
    else ckmax(ans, hi);
    ps(a, ans);
    return 0;
}

