using namespace std;
const int MAXN = 200005;
const LL M1 = 1000000009;
const LL M2 = 1000000007;

LL qpow(LL a, LL pw, LL mod) {
	LL res = 1;
	while (pw) {
		if (pw & 1) res = res * a % mod;
		pw >>= 1;
		a = a * a % mod;
	}
	return res;
}

PLL norm(PLL p) {
	return PLL((p.f % M1 + M1) % M1, (p.s % M2 + M2) % M2);
}

PLL neg(PLL p) {
	return PLL(M1 - p.f, M2 - p.s);
}

PLL get_inv(PLL p) {
	return PLL(qpow(p.f, M1 - 2, M1), qpow(p.s, M2 - 2, M2));
}

PLL mul(PLL p, PLL q) {
	return PLL(p.f * q.f % M1, p.s * q.s % M2);
}

PLL add(PLL p, PLL q) {
	return PLL((p.f + q.f) % M1, (p.s + q.s) % M2);
}

int n;
