		if (t == '+') pref[i] = add(pref[i], pw[pos[i]]);
		if (t == '-') pref[i] = add(pref[i], neg(pw[pos[i]]));
		cnt[pref[i]]++;
	}

	LL ans = 0; 
	PLL tgt = pref[n], mulf = PLL(1, 1), addf = PLL(0, 0);
