        cur = cur * b[c] % mods[c];
      }
      if (s[i] == '<') {
        p--;
        cur = cur * inv[c] % mods[c];
      }
      if (s[i] == '+') {
        tmp = (tmp + cur) % mods[c];
      }
      if (s[i] == '-') {
        tmp = (tmp + mods[c] - cur) % mods[c];
      }
      hsh[c][i + 1] = tmp;
      pos[i + 1] = p;
    }
  }
  if (DEBUG) {
