			// cerr << "add -1\n";
		}
	}
	for (int i = 0; i < N; i++)
	{
		if (arr[i] == 0)
		{
			if (S == MP(0ll, 0ll)) ans++;
		}
		if (arr[i] == 1)
		{
			if (S == MP(0ll, 0ll)) ans++;
		}
		if (arr[i] == 2)
		{
			if (S == MP(1ll, 1ll)) ans++;
		}
		if (arr[i] == 3)
		{
			if (S == MP((ll) INF - 1, (ll) BIG - 1)) ans++;
		}
	}
	solve(0, N - 1);
	cout << ans << '\n';
	//when u <, the # divides by co
