// Computes the inverse of `a` modulo 2^64.
uint64_t mod_inv_2_64(uint64_t a) {
    // https://en.wikipedia.org/wiki/Extended_Euclidean_algorithm#Example
    // https://www.di-mgt.com.au/euclidean.html#code-modinv
    uint64_t g = 0, r = a, x = 0, y = 1;
    int sign = -1;

    // Do the first iteration manually to avoid overflow issues.
    uint64_t u64_max = ((uint64_t) -1);
    uint64_t q = u64_max / r + (u64_max % r == r - 1 ? 1 : 0);
    g = u64_max % r == r - 1 ? 0 : u64_max % r + 1; swap(g, r);
    x += q * y; swap(x, y);
    sign = -sign;

    while (r != 0) {
        q = g / r;
        g %= r; swap(g, r);
        x += q * y; swap(x, y);
        sign = -sign;
    }

    assert(g == 1);
    assert(y == 0);
    return sign > 0 ? x : -x;
}

auto random_address = [] { char *p = new char; delete p; return (uint64_t) p; };
mt19937_64 rng(chrono::steady_clock::now().time_since_epoch().count() * (random_address() | 1));

using hash_t = uint64_t;

// HASH_MULT is a random number 3 or 5 mod 8. This ensures the hash is reliable until comparing strings of length 1024.
const hash_t HASH_MULT = (rng() & ~7) + (rng() % 2 ? 3 : 5);
const hash_t HASH_MULT_INV = mod_inv_2_64(HASH_MULT);

vector<hash_t> hash_pow = {1};
vector<hash_t> inv_pow = {1};


int N;
string S;

struct state {
    vector<int> A;
    int position;
    hash_t hash;

    state() {
        A.assign(2 * N + 1, 0);
        position = N;
        hash = 0;
    }

    void modify(int change) {
        assert(change == +1 || change == -1);
        A[position] += change;
        hash += change * hash_pow[position];
    }

    void move(int dir) {
        assert(dir == +1 || dir == -1);
        position += dir;
    }
};

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    assert(HASH_MULT * HASH_MULT_INV == 1);
    cin >> N >> S;

    for (int i = 0; i < 2 * N; i++)
        hash_pow.push_back(HASH_MULT * hash_pow.back());

    for (int i = 0; i < 2 * N; i++)
        inv_pow.push_back(HASH_MULT_INV * inv_pow.back());

    state current;
    vector<hash_t> prefix_hashes = {current.hash};

    for (int i = 0; i < N; i++) {
        if (S[i] == '+' || S[i] == '-')
            current.modify(S[i] == '+' ? +1 : -1);
        else
            current.move(S[i] == '>' ? +1 : -1);

        prefix_hashes.push_back(current.hash);
    }

    map<hash_t, int> freq;

    for (hash_t h : prefix_hashes)
        freq[h]++;

    hash_t goal = prefix_hashes.back();
    current = state();
    long long total = 0;

    for (int i = 0; i < N; i++) {
        freq[prefix_hashes[i]]--;

        hash_t mult = current.position >= N ? hash_pow[current.position - N] : inv_pow[N - current.position];
        hash_t target = prefix_hashes[i] + goal * mult;
        total += freq[target];

        if (S[i] == '+' || S[i] == '-')
            current.modify(S[i] == '+' ? +1 : -1);
        else
            current.move(S[i] == '>' ? +1 : -1);
    }

    cout << total << '\n';
}

#include<bits/stdc++.h>
using namespace std;
typedef long long D;
const int maxn=500005,base=17,mod1=998244353,mod2=1000000009;
int qpow(int x,int y,int mod){
	int ans=1;
	while(y){
		if(y&1)ans=D(ans)*x%mod;
		x=D(x)*x%mod;
		y>>=1;
	}
	return ans;
}
int n,temp[base+1];
char s[maxn];
struct H{
	int a,b;
	H():a(0),b(0){}
	H(int _a,int _b):a(_a),b(_b){}
	H operator+(const H &x)const{return H((a+x.a)%mod1,(b+x.b)%mod2);}
	H operator-(const H &x)const{return H((a-x.a+mod1)%mod1,(b-x.b+mod2)%mod2);}
	H operator*(const H &x)const{return H((D)a*x.a%mod1,(D)b*x.b%mod2);}
	bool operator<(const H &x)const{return a!=x.a?a<x.a:b<x.b;}
}b,inv,f[maxn],g[maxn];
map<H,int> mp;
int main(){
	b=H(base,base),inv=H(qpow(base,mod1-2,mod1),qpow(base,mod2-2,mod2));
	scanf("%d%s",&n,s+1);
	g[0]=H(1,1);
	for(int i=1;i<=n;i++){
		f[i]=f[i-1],g[i]=g[i-1];
		if(s[i]=='+')f[i]=f[i]+g[i];
		if(s[i]=='-')f[i]=f[i]-g[i];
		if(s[i]=='<')g[i]=g[i]*inv;
		if(s[i]=='>')g[i]=g[i]*b;
		mp[f[i]]++;
	}
	D ans=0;
	for(int i=1;i<=n;i++){
		ans+=mp[f[i-1]+g[i-1]*f[n]];
		mp[f[i]]--;
	}
	printf("%lld\n",ans);
	return 0;
}

