const ll MOD2 = 1000000007;

int n;
int p[MAXN];

ll inv1,inv2,ans;

pair<ll,ll> h[MAXN];
pair<ll,ll> pw[MAXN];
pair<ll,ll> rpw[MAXN];

char s[MAXN];

map<pair<ll,ll>,int> cnt;

ll power(ll a,ll b,ll p)
{
	ll res = 1;
	while (b)
	{
		if (b & 1)
			(res *= a) %= p;
		(a *= a) %= p;
		b >>= 1;
	}
	return res;
}

pair<ll,ll> getv(int t)
{
	return t >= 0 ? pw[t] : rpw[-t];
}

pair<ll,ll> mul(pair<ll,ll> a,pair<ll,ll> b)
{
	return make_pair(a.first * b.first % MOD1,a.second * b.second % MOD2);
}

pair<ll,ll> add(pair<ll,ll> a,pair<ll,ll> b)
{
	return make_pair((a.first + b.first) % MOD1,(a.second + b.second) % MOD2);
}

pair<ll,ll> sub(pair<ll,ll> a,pair<ll,ll> b)
{
	return make_pair((a.first - b.first) % MOD1,(a.second - b.second) % MOD2);
}

int main()
{
	scanf("%d%s",&n,s + 1);
	inv1 = power(786433,MOD1 - 2,MOD1);
	inv2 = power(786433,MOD2 - 2,MOD2);
	pw[0] = make_pair(1,1);
	for (int i = 1;i <= n;i++)
		pw[i] = mul(pw[i - 1],make_pair(786433,786433));
	rpw[0] = make_pair(1,1);
	for (int i = 1;i <= n;i++)
		rpw[i] = mul(rpw[i - 1],make_pair(inv1,inv2));
	for (int i = 1;i <= n;i++)
	{
		if (s[i] == '+')
		{
			p[i] = p[i - 1];
			h[i] = add(h[i - 1],getv(p[i]));
		}
		if (s[i] == '-')
		{
			p[i] = p[i - 1];
			h[i] = sub(h[i - 1],getv(p[i]));
		}
		if (s[i] == '<')
		{
			h[i] = h[i - 1];
			p[i] = p[i - 1] - 1;
		}
		if (s[i] == '>')
		{
			h[i] = h[i - 1];
			p[i] = p[i - 1] + 1;
		}
	}
	for (int i = n;i >= 1;i--)
	{
		(h[i].first += MOD1) %= MOD1;
		(h[i].second += MOD2) %= MOD2;
	}
	for (int i = n;i >= 1;i--)
	{
		cnt[h[i]]++;
		ans += cnt[add(mul(h[n],getv(p[i - 1])),h[i - 1])];
	}
	printf("%lld\n",ans);
	return 0;
}

