				rep(j, len) {
					sum[j] -= tr[loc][j];
				}
			}
			else {
				rep(j, len) {
					sum[j] -= invtr[-loc][j];
				}
			}
		}
		rep(j, len) {
			ll mod = mods[j / roots.size()];
			sum[j] = (sum[j] % mod + mod) % mod;
			memo[i][j] = sum[j];
		}

		locs[i] = loc;
	}
