int powMod(int base,int exp,int mod){
	if(exp<0)exp+=mod-1;
	int ans=1;
	for(;;){
		if(exp&1)ans=(int64_t)ans*base%mod;
		exp>>=1;if(exp==0)return ans;
		base=(int64_t)base*base%mod;
	}
}
struct prg{
	static int constexpr NPRIME=3;
	using value_t=std::array<int,NPRIME>;
	static std::array<int,NPRIME> constexpr PRIMES{{1073741789,1073741783,1073741741}};
	static int constexpr BASE=250007;

	value_t value;
	int offset;
	constexpr prg():value{},offset{}{}

	static prg const left,right,inc,dec;
private:
	constexpr prg(value_t v,int o):value(v),offset(o){
		for(int p=0;p<prg::NPRIME;++p){
			value[p]=value[p]%PRIMES[p];
			if(value[p]<0)value[p]+=PRIMES[p];
		}
	}
};

prg operator*(prg A,prg B){
	prg AB;
	AB.offset=A.offset+B.offset;
	for(int p=0;p<prg::NPRIME;++p){
		AB.value[p]=(A.value[p]+1LL*powMod(prg::BASE,A.offset,prg::PRIMES[p])*B.value[p])%prg::PRIMES[p];
	}
	return AB;
}

std::ostream& operator<<(std::ostream& stream,prg p){
	return stream<<p.value[0]<<'|'<<p.offset;
}

