	inline pH moveCenter(pH has, int nowCenter) {
		const int movement = A - nowCenter;
		for (int i : range(num)) {
			has[i] *= RS[movement][i];
		}
		return has;
	}
	inline pH shift(pH has, int sft) {
		const int movement = A +sft;
		for (int i : range(num)) {
			has[i] *= RS[movement][i];
		}
		return has;
	}
	inline void ins(pH has,int c) {
		cnt[{has[0], has[1]}] += c;
	}
	inline LL get(pH has) {
		return cnt[{has[0], has[1]}];
	}
}
int main() {
	using namespace RollHash;
	while (cin >> N >> S) {
		init();
		LL ret = 0;

		vector<pH> suf(N + 1);
		vector<int> sft(N + 1);
		sft[0] = 0;
		for (int i : range(N)) {
			sft[i + 1] = sft[i];
			if (S[i] == '>')sft[i + 1]++;
			if (S[i] == '<')sft[i + 1]--;
		}
		pH now{ 0,0 };
		int center = 0;
		suf[N] = shift(moveCenter(now, center), sft[N]);
		ins(suf[N], 1);
		for (int ri : range(N)) {
			const int i = N - 1 - ri;
			if (S[i] == '+')now = add(now, center);
			if (S[i] == '-')now = sub(now, center);
			if (S[i] == '>')center--;
			if (S[i] == '<')center++;
			suf[i]=shift(moveCenter(now, center),sft[i]);
			ins(suf[i], 1);
		}
		now = moveCenter(now, center);
		for (int i : range(N)) {
			ins(suf[i], -1);
			auto latte = suf[i];
			auto malta = shift(now, sft[i]);
			auto ei = sub(latte, malta);
			LL beet = get(ei);
			ret += beet;
		}

		cout << ret << endl;
	}
	return 0;
}

