hash_t operator +(const hash_t &a,const hash_t &b)
{
	return hash_t((a.first+b.first)%D1,(a.second+b.second)%D2);
}
hash_t operator -(const hash_t &a,const hash_t &b)
{
	return hash_t((a.first+D1-b.first)%D1,(a.second+D2-b.second)%D2);
