template<typename ty,typename ty2>
struct my_hash_map{
	#define hash_map_MX ((1<<20)-1)
	int head[hash_map_MX+2];
	int nex[hash_map_MX+2];
	ty key[hash_map_MX+2];
	ty2 assoc_val[hash_map_MX+2];
	int tot;
	my_hash_map(){
		tot=0;
		memset(head,-1,sizeof(head));
		memset(nex,-1,sizeof(nex));
	}
	inline ty2* find(ty val){
		int km=(val&hash_map_MX);
		if(head[km]==-1){
			head[km]=tot++;
			key[head[km]]=val;
			return &assoc_val[tot-1];
		}
		for(int i=head[km];;i=nex[i]){
			if(key[i]==val)return &assoc_val[i];
			if(nex[i]==-1){
				nex[i]=tot++;
				key[nex[i]]=val;
				return &assoc_val[tot-1];
			}
		}
		return NULL;
	}
};

