		scanf(" %c",&c);
		cs[i] = cs[i - 1];
		p[i] = p[i - 1];
		if(c == '+')add(cs[i],p[i]);
		else if(c == '-')add(cs[i],-p[i]);
		else if(c == '>')p[i] = p[i]*1LL*b%M;
		else if(c == '<')p[i] = p[i]*1LL*inv%M;
	}
	long long ans = 0;
	for(int i = 0;i <= n;i++){
		ans+=mp[cs[i]];
		mp[(cs[n]*1LL*p[i] + cs[i] + M)%M]++;
	}
	cout << ans << endl;
}

