// 
int extgcd(int a, int b, int &x, int &y) {
    int d = a;
    if (b != 0) {
        d = extgcd(b, a % b, y, x);
        y -= (a / b) * x;
    } else {
        x = 1;
        y = 0;
    }
    return d;
}

// mod
int mod_inverse(int a, int m) {
    int x, y;
    extgcd(a, m, x, y);
    return (m + x % m) % m;
}

pint f(pint ab, int i, int hash) {
    if (S[i] == '+') { return pint(ab.first, (ab.second + 1) % mod); }
    else if (S[i] == '-') { return pint(ab.first, (ab.second - 1 + mod) % mod); }
    else if (S[i] == '>') { return pint(ab.first * hash % mod, ab.second * hash % mod); }
    else if (S[i] == '<') {
        return pint(ab.first * mod_inverse(hash, mod) % mod,
                    ab.second * mod_inverse(hash, mod) % mod);
    }
}

pint finv(pint ab, int i, int hash) {
    if (S[i] == '-') { return pint(ab.first, (ab.second + 1) % mod); }
    else if (S[i] == '+') { return pint(ab.first, (ab.second - 1 + mod) % mod); }
    else if (S[i] == '<') { return pint(ab.first * hash % mod, ab.second * hash % mod); }
    else if (S[i] == '>') {
        return pint(ab.first * mod_inverse(hash, mod) % mod,
                    ab.second * mod_inverse(hash, mod) % mod);
    }
}
