        if (s[i] == '>') prefa[i] = (prefa[i - 1] * rK) % MOD;
        moves[i] = moves[i - 1];
        if (s[i] == '>') moves[i]++;
        if (s[i] == '<') moves[i]--;
    }
    for (int j = 0; j < n; j++)
    {
        if (s[j] == '+')
        {
            suf0[0] = (suf0[0] + 1) % MOD;
        }
        if (s[j] == '-')
        {
            suf0[0] = (suf0[0] + MOD - 1) % MOD;
        }
        if (s[j] == '<')
        {
            suf0[0] = (suf0[0] * K) % MOD;
        }
        if (s[j] == '>')
        {
            suf0[0] = (suf0[0] * rK) % MOD;
        }
    }
    for (int i = 0; i < n; i++) suf0[i + 1] = (suf0[0] - pref0[i] * deg[moves[i]] + MOD * MOD) % MOD;
    for (int i = 0; i < n; i++) sufa[i] = (suf0[i] + a * deg[(i > 0 ? moves[i - 1] : 0) - moves[n - 1]]) % MOD;
    #ifdef LOCAL
    for (int i = 0; i < n; i++) cout << pref0[i] * deg[moves[i]] % MOD << " "; cout << "\n";
    for (int i = 0; i < n; i++) cout << prefa[i] * deg[moves[i]] % MOD << " "; cout << "\n";
    for (int i = 0; i < n; i++) cout << suf0[i] << " "; cout << "\n";
    for (int i = 0; i < n; i++) cout << -(moves[n - 1] - moves[i - 1]) << " "; cout << "\n";
    for (int i = 0; i < n; i++) cout << (sufa[i] + MOD - suf0[i]) % MOD << " "; cout << "\n";
    cout << "This line\n";
    for (int i = 0; i < n; i++) cout << (suf0[i] + a * deg[moves[i - 1] - moves[n - 1]]) % MOD << " "; cout << "\n";
    for (int i = 0; i < n; i++) cout << sufa[i] << " "; cout << "\n";
    cout << "That line\n";
    for (int i = 0; i < n; i++) cout << (pref0[i] * deg[moves[i]] + suf0[i + 1]) % MOD << " "; cout << "\n";
    for (int i = 0; i < n; i++) cout << (pref0[i] * deg[moves[i]] + sufa[i + 1]) % MOD << " "; cout << "\n";
    #endif // LOCAL
    ll ans = 0;
    map<vector<int>, int> cnt;
    for (int i = n; i >= 0; i--)
    {
        ans += cnt[{sufa[i]}];
        cnt[{suf0[i]}]++;
    }
    cout << ans;
}

