vec shift(vec x, int val) { // to the right
	if (val > 0) {
		reverse(all(x));
		while (val--) {
			x.pb(0);
		}
		reverse(all(x));
	} else {
		reverse(all(x));
		val *= -1;
		while (val--) {
			x.pop_back();
		}
		reverse(all(x));
	}
	while (sz(x) < 4 * n) x.pb(0);
	while (sz(x) > 4 * n) x.pop_back();
	return x;
}

vec add(vec x, vec y) {
	assert(sz(x) == sz(y));
	for (int i = 0; i < sz(x); i++) {
		x[i] += y[i];
	}
	return x;
}

