			arr[i] = 1;
		}
		if (temps[i] == '+')
		{
			arr[i] = 2;
		}
		if (temps[i] == '-')
		{
			arr[i] = 3;
		}
	}
	reverse(arr, arr + N);
	for (int i = 0; i < N; i++)
	{
		if (arr[i] == 0)
		{
			S.fi *= INV.fi; S.fi %= INF;
			S.se *= INV.se; S.se %= BIG;
		}
		if (arr[i] == 1)
		{
			S.fi *= CO; S.fi %= INF;
			S.se *= CO; S.se %= BIG;
		}
		if (arr[i] == 2)
		{
			S.fi++; nmod(S.fi, INF);
			S.se++; nmod(S.se, BIG);
		}
		if (arr[i] == 3)
		{
			S.fi--; S.fi += INF; nmod(S.fi, INF);
			S.se--; S.se += BIG; nmod(S.se, BIG);
		}
	}
	for (int i = 0; i < N; i++)
	{
		if (arr[i] == 0)
		{
			if (S == MP(0ll, 0ll)) ans++;
		}
		if (arr[i] == 1)
		{
			if (S == MP(0ll, 0ll)) ans++;
		}
		if (arr[i] == 2)
		{
			if (S == MP(1ll, 1ll)) ans++;
		}
		if (arr[i] == 3)
		{
			if (S == MP((ll) INF - 1, (ll) BIG - 1)) ans++;
		}
	}
	solve(0, N - 1);
	cout << ans << '\n';
	//when u <, the # divides by co
	//when u >, the # mults by co
	//when u +, u add the #
	//when u -, u subtract the #
	// cerr << "time elapsed = " << (clock() / (CLOCKS_PER_SEC / 1000)) << " ms" << endl;
	return 0;
}

