        if (s[i]=='>') {
            b4 = compose(b4, lfunc(invb, zero));
        } else if (s[i]=='<') {
            b4 = compose(b4, lfunc(PLL(base, base), zero));
        } else if (s[i]=='+') {
            b4 = compose(b4, lfunc(one, _one));
        } else {
            assert(s[i]=='-');
            b4 = compose(b4, lfunc(one, one));
        }
    }

    cout << ans << endl;

    return 0;
}

