const int mod1=998244353,g1=504510733,ig1=799218450;
const int mod2=1000000009,g2=486738396,ig2=567937635;

int n;
char C[600000];

int fha1,fha2,pw1[600000],pw2[600000],ipw1[600000],ipw2[600000];
inline void chkinc(int &C,int P){C+=C>>31&P;}
long long ans;

void solve(int L,int R){
	if (L==R){
		switch (C[L]){
			case '<':
				ans+=!fha1&&!fha2;
				break;
			case '>':
				ans+=!fha1&&!fha2;
				break;
			case '-':
				ans+=(mod1-pw1[250000]==fha1&&mod2-pw2[250000]==fha2);
				break;
			case '+':
				ans+=(pw1[250000]==fha1&&pw2[250000]==fha2);
				break;
		}
	}
	else{
		int mdl=(L+R)>>1;
		solve(L,mdl);
		solve(mdl+1,R);
		int ha1=0,ha2=0,pos=250000;
		std::map<std::pair<int,int>,int> M;
		for (int i=mdl+1;i<=R;i++){
			switch (C[i]){
				case '<':
					pos--;
					break;
				case '>':
					pos++;
					break;
				case '-':
					chkinc(ha1-=pw1[pos],mod1);
					chkinc(ha2-=pw2[pos],mod2);
					break;
				case '+':
					chkinc(ha1+=pw1[pos]-mod1,mod1);
					chkinc(ha2+=pw2[pos]-mod2,mod2);
					break;
			}
			M[std::make_pair(ha1,ha2)]++;
		}
		ha1=0,ha2=0,pos=0;
		for (int i=mdl;i>=L;i--){
			switch (C[i]){
				case '<':
					pos--;
					ha1=static_cast<long long>(ha1)*ig1%mod1;
					ha2=static_cast<long long>(ha2)*ig2%mod2;
					break;
				case '>':
					pos++;
					ha1=static_cast<long long>(ha1)*g1%mod1;
					ha2=static_cast<long long>(ha2)*g2%mod2;
					break;
				case '-':
					chkinc(ha1-=pw1[250000],mod1);
					chkinc(ha2-=pw2[250000],mod2);
					break;
				case '+':
					chkinc(ha1+=pw1[250000]-mod1,mod1);
					chkinc(ha2+=pw2[250000]-mod2,mod2);
					break;
			}
			if (pos>=0){
				int a1=static_cast<long long>(fha1-ha1+mod1)*ipw1[pos]%mod1;
				int a2=static_cast<long long>(fha2-ha2+mod2)*ipw2[pos]%mod2;
				ans+=M[std::make_pair(a1,a2)];
			}
			else{
				int a1=static_cast<long long>(fha1-ha1+mod1)*pw1[-pos]%mod1;
				int a2=static_cast<long long>(fha2-ha2+mod2)*pw2[-pos]%mod2;
				ans+=M[std::make_pair(a1,a2)];
			}
		}
	}
}

int main(){
	scanf("%d",&n);
	scanf("%s",C);
	pw1[0]=pw2[0]=ipw1[0]=ipw2[0]=1;
	for (int i=1;i<=500000;i++){
		pw1[i]=static_cast<long long>(pw1[i-1])*g1%mod1;
		pw2[i]=static_cast<long long>(pw2[i-1])*g2%mod2;
		ipw1[i]=static_cast<long long>(ipw1[i-1])*ig1%mod1;
		ipw2[i]=static_cast<long long>(ipw2[i-1])*ig2%mod2;
	}
	for (int i=0,P=250000;i<n;i++){
		switch (C[i]){
			case '<':
				P--;
				break;
			case '>':
				P++;
				break;
			case '-':
				chkinc(fha1-=pw1[P],mod1);
				chkinc(fha2-=pw2[P],mod2);
				break;
			case '+':
				chkinc(fha1+=pw1[P]-mod1,mod1);
				chkinc(fha2+=pw2[P]-mod2,mod2);
				break;
		}
	}
	solve(0,n-1);
	printf("%lld\n",ans);
}

