unordered_map<ll, int> need;

ll expo(ll a, ll e, ll mod)
{
	if (e == 0)
	{
		return 1;
	}
	ll was = expo(a, e / 2, mod);
	if (e % 2)
	{
		return was * was % mod * a % mod;
	}
	return was * was % mod;
}
ll modinv(ll a, ll e)
{
	return expo(a, e - 2, e);
}
pll INV = {modinv(CO, INF), modinv(CO, BIG)};
void solve(int L, int R)
{
	if (L == R)
	{
		return;
	}
	need.clear();
	int mid = (L + R) >> 1;
	//start from L...mid, end at mid + 1....R
	pair<pll, pll> trans = MP(MP(1, 0), MP(1, 0));
	for (int i = mid + 1; i <= R; i++)
	{
		if (arr[i] == 0)
		{
			trans.fi.fi *= INV.fi; trans.fi.fi %= INF;
			trans.fi.se *= INV.fi; trans.fi.se %= INF;
			trans.se.fi *= INV.se; trans.se.fi %= BIG;
			trans.se.se *= INV.se; trans.se.se %= BIG;
		}
		if (arr[i] == 1)
		{
			trans.fi.fi *= CO; trans.fi.fi %= INF;
			trans.fi.se *= CO; trans.fi.se %= INF;
			trans.se.fi *= CO; trans.se.fi %= BIG;
			trans.se.se *= CO; trans.se.se %= BIG;
		}
		if (arr[i] == 2)
		{
			trans.fi.se++; nmod(trans.fi.se, INF);
			trans.se.se++; nmod(trans.se.se, BIG);
		}
		if (arr[i] == 3)
		{
			trans.fi.se--; trans.fi.se += INF; nmod(trans.fi.se, INF);
			trans.se.se--; trans.se.se += BIG; nmod(trans.se.se, BIG);
		}
		pll dif = {(S.fi - trans.fi.se) * modinv(trans.fi.fi, INF), (S.se - trans.se.se) * modinv(trans.se.fi, BIG)};
		dif.fi %= INF; dif.fi += INF; nmod(dif.fi, INF);
		dif.se %= BIG; dif.se += BIG; nmod(dif.se, BIG);
		need[dif.fi * BIG + dif.se]++;
	}
	trans = MP(MP(1, 0), MP(1, 0));
	for (int i = mid; i >= L; i--)
	{
		if (arr[i] == 0)
		{
			trans.fi.fi *= INV.fi; trans.fi.fi %= INF;
			trans.se.fi *= INV.se; trans.se.fi %= BIG;
		}
		if (arr[i] == 1)
		{
			trans.fi.fi *= CO; trans.fi.fi %= INF;
			trans.se.fi *= CO; trans.se.fi %= BIG;
		}
		if (arr[i] == 2)
		{
			trans.fi.se += trans.fi.fi; nmod(trans.fi.se, INF);
			trans.se.se += trans.se.fi; nmod(trans.se.se, BIG);
		}
		if (arr[i] == 3)
		{
			trans.fi.se -= trans.fi.fi; trans.fi.se += INF; nmod(trans.fi.se, INF);
			trans.se.se -= trans.se.fi;	trans.se.se += BIG; nmod(trans.se.se, BIG);
		}
		ans += need[trans.fi.se * BIG + trans.se.se];
	}
	solve(L, mid);
	solve(mid + 1, R);
}

