void precalc() {
	pw[M] = PolyHash(1);
	pw[M + 1] = PolyHash(1234567);
	pw[M - 1].x[0] = bin_pow(1234567, MODS[0] - 2, MODS[0]);
	pw[M - 1].x[1] = bin_pow(1234567, MODS[1] - 2, MODS[1]);
	for (int i = M + 2; i < N; i++)
