	{
		return (Hsh){(m1 - a.m1 + mod1) % mod1,(m2 - a.m2 + mod2) % mod2};
	}
	Hsh operator * (const Hsh &a) const
	{
		return (Hsh){m1 * a.m1 % mod1,m2 * a.m2 % mod2};
	}
	bool operator < (const Hsh &a) const
