#include <bits/stdc++.h>

using namespace std;

typedef long long ll;

const int MAX_N = 3e5 + 10, X_NUM = 2;
const ll MOD = 1e9 + 7;

ll sq(ll x) { return x * x % MOD; }
ll qpow(ll a, ll b) { return b ? sq(qpow(a, b / 2)) * (b & 1 ? a : 1) % MOD : 1; }
ll inv(ll x) { return qpow(x, MOD - 2); }

vector<ll> values_x = {100, 10000};
vector<ll> values_invx;
vector<pair<ll, ll>> revert[MAX_N];
vector<ll> digest[MAX_N];

int N;
char S[MAX_N];
map<vector<ll>, int> occur;
ll ans;

void init() {
    scanf("%d", &N); scanf("%s", S + 1);
    for (int i = 0; i < X_NUM; i++) values_invx.push_back(inv(values_x[i]));
    for (int i = 0; i < X_NUM; i++) {
        ll v = 0, px = 1;
        for (int j = 1; j <= N; j++) {
            switch (S[j]) {
            case '<': {
                (px *= values_invx[i]) %= MOD;
            }; break;
            case '>': {
                (px *= values_x[i]) %= MOD;
            }; break;
            case '+': {
                (v += px) %= MOD;
            }; break;
            case '-': {
                (v += MOD - px) %= MOD;
            }; break;
            }
            digest[j].push_back(v);
        }
    }
    for (int i = 0; i < X_NUM; i++) {
        ll a = 1, b = 0; // ax + b
        revert[0].emplace_back(a, b);
        for (int j = 1; j <= N; j++) {
            switch (S[j]) {
            case '<': {
                (a *= values_x[i]) %= MOD;
                (b *= values_x[i]) %= MOD;
            }; break;
            case '>': {
                (a *= values_invx[i]) %= MOD;
                (b *= values_invx[i]) %= MOD;
            }; break;
            case '+': {
                (b += MOD - 1) %= MOD;
            }; break;
            case '-': {
                (b += 1) %= MOD;
            }; break;
            }
            revert[j].emplace_back(a, b);
        }
    }
    for (int i = 1; i <= N; i++)
        occur[digest[i]]++;
}

void solve() {
    for (int i = 0; i < N; i++) {
        vector<ll> target;
        for (int j = 0; j < X_NUM; j++) {
            ll t = (digest[N][j] - revert[i][j].second + MOD) * inv(revert[i][j].first) % MOD;
            target.push_back(t);
        }
        ans += occur[target];
        occur[digest[i + 1]]--;
    }
    printf("%lld\n", ans);
}

int main() {
    init();
    solve();
}

