void init() {
	hsh[0] = 0, ps[0] = 1;
	rep (i, 1, n) {
		if (s[i] == '+') {
			ps[i] = ps[i-1];
			hsh[i] = (hsh[i-1] + ps[i]) % O;
		}
		else if (s[i] == '-') {
			ps[i] = ps[i-1];
			hsh[i] = (hsh[i-1] + O - ps[i]) % O;
		}
		else if (s[i] == '<') {
			ps[i] = 1LL * ps[i-1] * _W % O;
			hsh[i] = hsh[i-1];
		}
		else if (s[i] == '>') {
			ps[i] = 1LL * ps[i-1] * W % O;
			hsh[i] = hsh[i-1];
		}
		else assert(false);
	}
}

