	return was * was % mod;
}
ll modinv(ll a, ll e)
{
	return expo(a, e - 2, e);
}
pll INV = {modinv(CO, INF), modinv(CO, BIG)};
void solve(int L, int R)
{
	if (L == R)
	{
		return;
	}
	freq.clear(); need.clear();
	int mid = (L + R) >> 1;
	//start from L...mid, end at mid + 1....R
	pair<pll, pll> trans = MP(MP(1, 0), MP(1, 0));
	for (int i = mid + 1; i <= R; i++)
	{
		if (arr[i] == 0)
		{
			trans.fi.fi *= INV.fi;
			trans.fi.se *= INV.fi;
			trans.se.fi *= INV.se;
			trans.se.se *= INV.se;
		}
		if (arr[i] == 1)
		{
			trans.fi.fi *= CO;
			trans.fi.se *= CO;
			trans.se.fi *= CO;
			trans.se.se *= CO;
		}
		if (arr[i] == 2)
		{
			trans.fi.se++;
			trans.se.se++;
		}
		if (arr[i] == 3)
		{
			trans.fi.se--; trans.fi.se += INF;
			trans.se.se--; trans.se.se += BIG;
		}
		trans.fi.fi %= INF; trans.fi.se %= INF;
		trans.se.fi %= BIG; trans.se.se %= BIG;
		// cerr << "from " << mid + 1 << " to " << i << " is " << trans.fi << "x + " << trans.se << endl;
		pll dif = {S.fi - trans.fi.se + INF, S.se - trans.se.se + BIG};
		nmod(dif.fi, INF); nmod(dif.se, BIG);
		dif.fi *= modinv(trans.fi.fi, INF);
		dif.se *= modinv(trans.se.fi, BIG);
		dif.fi %= INF; dif.se %= BIG;
		need[dif]++;
	}
	trans = MP(MP(1, 0), MP(1, 0));
	for (int i = mid; i >= L; i--)
	{
		if (arr[i] == 0)
		{
			trans.fi.fi *= INV.fi;
			trans.se.fi *= INV.se;
		}
		if (arr[i] == 1)
		{
			trans.fi.fi *= CO;
			trans.se.fi *= CO;
		}
		if (arr[i] == 2)
		{
			//(x+1)*a+b = a * x + a + b
			trans.fi.se += trans.fi.fi;
			trans.se.se += trans.se.fi;
		}
		if (arr[i] == 3)
		{
			//(x - 1)*a + b = a*x - a + b
			trans.fi.se -= trans.fi.fi;
			trans.fi.se += INF;
			trans.se.se -= trans.se.fi;
			trans.se.se += BIG;
		}
		trans.fi.fi %= INF; trans.fi.se %= INF;
		trans.se.fi %= BIG; trans.se.se %= BIG;
		freq[{trans.fi.se, trans.se.se}]++;
	}
	for (auto p : need)
	{
		pll x = p.fi; int c = p.se;
		ans += 1ll * c * freq[x];
	}
	solve(L, mid);
	solve(mid + 1, R);
}

