			pref[i]=pref[i]+pot[i];
		else{
			int a=pot[i].first;
			int b=pot[i].second;
			pref[i]=pref[i]+pii(-a,-b);
		}
	}
	ll ans=0;
	for(int i=N;i;--i){
		++cnt[pref[i]];
		pii a=pref[N];
		ans+=cnt[(a*pot[i-1])+pref[i-1]];
	}
	return ans;
}

