const int64_t MOD = 67280421310721;

int n, cnt[MAX_N];
string s;
int64_t pref_hash[MAX_N], suff_hash[MAX_N];

int64_t mul(int64_t a, int64_t b) {
	int64_t res = 0;
	while (b) {
		if (b%2)
			res = (res + a) % MOD;
		a = a * 2 % MOD;
		b = b / 2;
	}
	return res;
}

void enter() {
	cin >> n >> s;
}

int64_t pw(int n, int k) {
	if (k==0)
		return 1;
	int64_t tmp = pw(n, k/2);
	if (k%2)
		return mul(mul(tmp, tmp), n);
	return mul(tmp, tmp);
}

void init_pref() {
	int p = offset;
	int64_t H = 0;
	for (int i=0; i<n; ++i) {
		if (s[i]=='<' || s[i]=='>') {
			p += (s[i]=='<' ? -1 : 1);
			cnt[i] = cnt[i-1] + (s[i]=='<' ? -1 : 1);
		}
		else {
			H = (H - pw(T, p) * (s[i]=='-' ? 1 : -1)) % MOD;
		}
		pref_hash[i] = (H + MOD) % MOD;
	}
}

void init_suff() {
	int64_t H = pref_hash[n-1];
	int p = offset;
	for (int i=0; i<n; ++i) {
		if (s[i]=='>' || s[i]=='<')
			p += (s[i]=='>' ? 1 : -1);
		else
			H = (H + pw(T, p) * (s[i]=='+' ? -1 : 1)) % MOD;
		suff_hash[i+1] = (H + MOD) % MOD;
	}
}

int64_t solve() {
	map<int64_t, int> mp;
	int64_t H = pref_hash[n-1];
	int64_t res = 0;
	for (int j=0; j<n; ++j) {
		if (j==0)
			++mp[0];
		else
			++mp[((H - pref_hash[j-1] - mul(H, pw(T, cnt[j-1]))) % MOD + MOD) % MOD];
		res += mp[suff_hash[j+1]];
	}
	return res;
}

