      h2[i] = (h2[i - 1] + pw2[s[i]]) % mod2;
    } else if(ch == '-') {
      s[i] = s[i - 1];
      h1[i] = (h1[i - 1] - pw1[s[i]] + mod1) % mod1;
      h2[i] = (h2[i - 1] - pw2[s[i]] + mod2) % mod2;
    } else if(ch == '>') {
      s[i] = s[i - 1] + 1;
      h1[i] = h1[i - 1];
      h2[i] = h2[i - 1];
    } else {
      s[i] = s[i - 1] - 1;
      h1[i] = h1[i - 1];
      h2[i] = h2[i - 1];
    }
    mp[P(h1[i], h2[i])]++;
  }
  ll ans = 0;
  for(int i = 1; i <= n; ++i) {
    int pos = h1[n], tmp = h2[n];
