/* find a pair (c,d) s.t. ac + bd = gcd(a,b)
* Dependencies: none */
pair<long long, long long> extended_gcd(long long a, long long b) {
  if (b == 0) return make_pair(1, 0);
  pair<long long, long long> t = extended_gcd(b, a % b);
  return make_pair(t.second, t.first - t.second * (a / b));
}

/* Find x in [0,m) s.t. ax  gcd(a, m) (mod m)
* Dependencies: extended_gcd(a, b) */
long long modinverse(long long a, long long m) {
  return (extended_gcd(a, m).first % m + m) % m;
}

struct LinearFunction {
  // Ax + B
  long long A, B;
  long long mod;
  long long x, xinv;

  LinearFunction(long long mod, long long x) : A(1), B(0), mod(mod), x(x) {
    xinv = modinverse(x, mod);
  }

  long long evaluate(long long seed) const {
    return (((A % mod) * (seed % mod) + B) % mod + mod) % mod;
  }

  long long evaluateInverse(long long seed) const {
    // Ay + B = seed
    // Ay = seed - B
    // y = (seed - B) * A^-1
    long long result = seed - B;
    result *= modinverse(A, mod);
    return (result % mod + mod) % mod;
  }

  void applyPlus() { B++; B %= mod; }
  void applyMinus() { B += mod - 1; B %= mod; }
  void applyLeft() { A = (A * xinv) % mod; B = (B * xinv) % mod; }
  void applyRight() { A = (A * x) % mod; B = (B * x) % mod; }
};

