const int X = 229;

struct hasher {
    vector<int> pw;
    int M;
    int val;

    int add(int a, int b) {
        return (a + b) % M;
    }

    int sub(int a, int b) {
        return (a - b + M) % M;
    }

    int mul(int a, int b) {
        return (int)(((ll)a * b) % M + M) % M;
    }

    int bp(int a, int b) {
        int r = 1;
        while(b) {
            if(b & 1) {
                r = mul(r, a);
            }
            a = mul(a, a);
            b >>= 1;
        }
        return r;
    }

    int div(int a, int b) {
        return mul(a, bp(b, M - 2));
    }

    hasher(int p, int m, int n) : M(m), val(0) {
        pw.resize(n);
        pw[0] = 1;
        for(int i = 1; i < n; ++i) {
            pw[i] = mul(pw[i - 1], p);
        }
    }

    void ins(int p, int x) {
        val = add(val, mul(pw[p], x));
    }

    void ers(int p, int x) {
        val = sub(val, mul(pw[p], x));
    }
};

inline void _main_function()
{
    int n;
    cin >> n;

    string s;
    cin >> s;

    vector<int> a(2 * n + 1);
    int p = n;

    hasher h(1000 * 1000 + 3, 1000 * 1000 * 1000 + 7, sz(a));

    vector<int> ans = {0};

    for(int i = 0; i < n; ++i) {
        if(s[i] == '<') {
