        } else if(s[i] == '>') {
            ++p;
        } else {
            h.ers(p, a[p]);
            if(s[i] == '+') {
                ++a[p];
            } else if(s[i] == '-') {
                --a[p];
            } else {
                assert(false);
            }
            h.ins(p, a[p]);
        }

        ans.push_back(h.val);
    }

    ll sum = 0;
    map<int, int> cnt;
    int sh = 0;

    for(int i = 0; i <= n; ++i) {
        sum += cnt[ans[i]];

        if(i && s[i - 1] == '>') {
            ++sh;
        } else if(i && s[i - 1] == '<') {
