const Int N = 300000;

constexpr ll mod_pow(ll x, ll n, Int mod){
  return n == 0? 1:(n%2? x:1) * mod_pow((x*x)%mod,n/2, mod) % mod;
}  

constexpr ll mod_inv(Int num,Int mod){return mod_pow(num, mod-2, mod);}
const ll mod[] = {1000000007,1000000009,1000000021};
const ll B[]= {1777771,10000019,10000079};
const ll inv[] = {mod_inv(B[0],mod[0]), mod_inv(B[1],mod[1]),mod_inv(B[2],mod[2])};

class Hash{
public:

  ll num;
  ll idx;
  vector<ll> K;
  Int b;
  Hash(Int b):b(b){
    K.resize(N);
    K[0] = 1;
    for(Int i=1;i<N;i++) K[i] = K[i-1] * B[b] % mod[b];
    num = 0;
    idx = K[N/2];
  }

  void inc(){num = (num + idx) % mod[b];}
  void dec(){num = (num - idx + mod[b]) % mod[b];}
  void moveLeft(){idx = idx * inv[b] % mod[b];}
  void moveRight(){idx = idx * B[b] % mod[b];}

};


signed main(){
  cin.tie(0);
  ios_base::sync_with_stdio(0);
  cout << fixed << setprecision(12);

  Int n;
  cin>>n;
  string s;
  cin>>s;

  vector<map<ll,Int> > cnt(3);
  vector<Hash> hash{Hash(0), Hash(1), Hash(2)};
  vector<Hash> hash2 = hash;

  for(Int k = 0; k<n ; k++){
    char ch = s[k];
    for(Int i=0;i<3;i++){
      auto &A = hash[i];
      if(ch == '+') A.inc();
      if(ch == '-') A.dec();
      if(ch == '<') A.moveLeft();
      if(ch == '>') A.moveRight();
      cnt[i][A.num]++;
    }
  }

  vector<ll> allHash(3);
  vector<ll> Ofset(3,0);
  for(Int i=0;i<3;i++) allHash[i] = hash[i].num;

  Int ans = 0;
  for(Int k=0;k<n;k++){

    char ch = s[k];
    Int mn = INF;
    for(Int i=0;i<3;i++){
      auto &A = hash2[i];
      auto &U = allHash[i];
      auto &X = Ofset[i];
      ll val = (U - X + mod[i]) % mod[i];
      Min(mn, cnt[i][val]);
      pr(U, X, (U + X)%mod[i], A.num);
      if(ch == '+') X = (X - A.idx + mod[i]) % mod[i], A.inc();
      if(ch == '-') X = (X + A.idx + mod[i]) % mod[i], A.dec();
      if(ch == '>') U = U * B[i] % mod[i], A.moveRight();
      if(ch == '<') U = U * inv[i] % mod[i], A.moveLeft();
      assert(cnt[i][A.num] != 0);
      cnt[i][A.num]--;
    }
    ans += mn;
  }
  cout<<ans<<endl;


  return 0;
}

