const int MOD = 1e9 + 9, N = 3e5 + 5;
const ll X = 1234567;

ll bp(ll a, ll p) {
    ll r = 1;
    while (p > 0) {
        if (p & 1) {
            r = r * a % MOD;
        }
        a = a * a % MOD;
        p >>= 1;
    }
    return r;
}

ll inv_X = bp(X, MOD - 2);

ll a[N], b[N];

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int n;
    string s;
    cin >> n >> s;

    ll h = 0;

    for (int i = n - 1; i >= 0; i--) {
        if (s[i] == '>')
            h = h * X % MOD;
        else if (s[i] == '<')
            h = h * inv_X % MOD;
        else if (s[i] == '+')
            h = (h + 1) % MOD;
        else
            h = (h - 1 + MOD) % MOD;
    }

    map<ll, int> mp;

    ll ans = 0;

    mp[0]++;

    a[n] = 1;

    for (int i = n - 1; i >= 0; i--) {
        a[i] = a[i + 1];
        b[i] = b[i + 1];

        if (s[i] == '>') {
            a[i] = a[i + 1] * inv_X % MOD;
            b[i] = b[i + 1];
        }
        else if (s[i] == '<') {
            a[i] = a[i + 1] * X % MOD;
            b[i] = b[i + 1];
        }
        else if (s[i] == '+') {
            a[i] = a[i + 1];
            b[i] = (b[i + 1] - a[i + 1] + MOD) % MOD;
        }
        else {
            a[i] = a[i + 1];
            b[i] = (b[i + 1] + a[i + 1]) % MOD;
        }

        // cout << b[i] << "\n";

        ans += mp[(a[i] * h + b[i]) % MOD];
        mp[b[i]]++;
    }

    // cout << (a[0] * h + b[0]) % MOD << "\n";

    cout << ans << "\n";

    return 0;   
}

