namespace Rd {
    inline bool gt() {
        int a = rand(), b = rand();
        while (a == b) a = rand(), b = rand();
        return a < b;
    }
    template <class I>
    inline I rd(I k) {
        I w = 1;
        while (w < k) w <<= 1;
        while (w >= k) {
            I o = 1, x = 0;
            while (o < k) o <<= 1, x = x << 1 | gt();
            w = x;
        }
        return w;
    }
    template <class I>
    inline I rdm(I l, I r) {
        return l == r ? l : l + rd(r - l + 1);
    }
    template <class I>
    inline void rdms(I *a, int n) {
        for (int i = n; i; i--) swap(a[i], a[rdm(1,i)]);
    }
}
