	inline friend bool operator < (H x, H y) { return x.a < y.a; }
	inline friend H operator + (H x, H y) {
		for (int i = 0; i < M; i++) x[i] += y[i];
		return x;
	}
	inline friend H operator - (H x, H y) {
		for (int i = 0; i < M; i++) x[i] -= y[i];
