const llint p[] = {2, 1145143, 998244353, 101, 640099, 2172113};
llint invp[6];

llint modpow(llint a, llint n)
{
	if(n == 0) return 1;
	if(n % 2){
		return ((a%mod) * (modpow(a, n-1)%mod)) % mod;
	}
	else{
		return modpow((a*a)%mod, n/2) % mod;
	}
}

int main(void)
{
	cin >> n >> s;
	reverse(s.begin(), s.end());
	s = "#" + s;

	for(int i = 0; i < 6; i++){
		invp[i] = modpow(p[i], mod-2);
		a[i][0] = 1, b[i][0] = 0;
	}
	for(int z = 0; z < 6; z++){
		for(int i = 1; i <= n; i++){
			a[z][i] = a[z][i-1], b[z][i] = b[z][i-1];
			if(s[i] == '+') b[z][i]++, b[z][i] %= mod;
			if(s[i] == '-') b[z][i] += mod-1, b[z][i] %= mod;
			if(s[i] == '<'){
				a[z][i] *= p[z], a[z][i] %= mod;
				b[z][i] *= p[z], b[z][i] %= mod;
			}
			if(s[i] == '>'){
				a[z][i] *= invp[z], a[z][i] %= mod;
				b[z][i] *= invp[z], b[z][i] %= mod;
			}
		}
	}

	llint ans = 0;
	for(int i = 0; i <= n; i++){
		vector<llint> vec;
		for(int j = 0; j < 6; j++){
			llint tmp = (b[j][i]-b[j][n]+mod) % mod;
			tmp *= modpow(a[j][i], mod-2), tmp %= mod;
			vec.push_back(tmp);
		}
		ans += mp[vec];
		vec.clear();
		for(int j = 0; j < 6; j++) vec.push_back(b[j][i] * modpow(a[j][i], mod-2) % mod);
		mp[vec]++;
	}
	cout << ans << endl;

	return 0;
}

