const mint B = 998244353;
mint ex(int p){
	bool m = 0;
	if(p<0){
		p = -p;
		m = 1;
	}
	mint a = 1;
	mint x = B;
	while(p){
		if(p%2==1) a *= x;
		x *= x;
		p/=2;
	}
	if(m) a = (mint)1/a;
	return a;
}

