vector<ll> ad(vector<ll> a)
{
    for (int i = 0; i < M; i++)
    {
        a[i]++;
        if (a[i] >= MOD) a[i] -= MOD;
    }
    return a;
}

vector<ll> mi(vector<ll> a)
{
    for (int i = 0; i < M; i++)
    {
        a[i]--;
        if (a[i]  < 0) a[i] += MOD;
    }
    return a;
}

vector<ll> mu(vector<ll> a)
{
    for (int i = 0; i < M; i++) a[i] = a[i] * K[i] % MOD;
    return a;
}

vector<ll> di(vector<ll> a)
{
    for (int i = 0; i < M; i++) a[i] = a[i] * rK[i] % MOD;
    return a;
}

