		pre[i] = pre[i - 1];
		if (s[i] == '>') {
			cnt[i] = cnt[i] * X % mo;
		} else if (s[i] == '<') {
			cnt[i] = cnt[i] * X1 % mo;
		} else if (s[i] == '+') {
			pre[i] = (pre[i] + cnt[i]) % mo;
		} else {
			pre[i] = (pre[i] - cnt[i]) % mo;
		}
