            b[i] = (b[i + 1] - a[i + 1] + MOD) % MOD;
        }
        else {
            a[i] = a[i + 1];
            b[i] = (b[i + 1] + a[i + 1]) % MOD;
        }

        // cout << b[i] << "\n";

        ans += mp[(a[i] * h + b[i]) % MOD];
    }

    // cout << (a[0] * h + b[0]) % MOD << "\n";

    cout << ans << "\n";

    return 0;   
}

#include <bits/stdc++.h>
using namespace std;
using lint = long long;

const int MAXN = 250005;
lint mod = 1e16 + 61;
const int base = 257;
int n, pos[MAXN];
char str[MAXN];
lint pwr[MAXN * 2];
lint inv[MAXN * 2];

lint ipow(lint x, lint p){
  lint ret = 1, piv = x;
  while(p){
    if(p & 1) ret = (__int128) ret * piv % mod;
    piv = (__int128) piv * piv % mod;
    p >>= 1;
  }
  return ret;
}

lint psum[MAXN];

int main(){
  mod++;
  cin >> n >> (str + 1);
  pwr[0] = inv[0] = 1;
  for(int i=1; i<MAXN * 2; i++){
    pwr[i] = pwr[i-1] * base % mod;
    inv[i] = ipow(pwr[i], mod - 2);
  }
  pos[0] = n;
  for(int i=1; i<=n; i++){
    pos[i] = pos[i-1];
    psum[i] = psum[i-1];
    if(str[i] == '<') pos[i]--;
    if(str[i] == '>') pos[i]++;
    if(str[i] == '+') psum[i] += pwr[pos[i]];
    if(str[i] == '-') psum[i] += mod - pwr[pos[i]];
    psum[i] %= mod;
  }
  lint H = psum[n];
  lint ret = 0;
  map<int, int> mp;
  for(int i=n; i; i--){
    mp[psum[i]]++;
    __int128 Hv = H;
    if(pos[0] <= pos[i-1]) Hv *= pwr[pos[i-1] - pos[0]];
    else Hv *= inv[pos[0] - pos[i-1]];
    Hv += psum[i-1];
    Hv %= mod;
    ret += mp[Hv];
  }
  cout << ret << endl;
}

