                return p == b.p;
            }
            inline bool operator != (iterator b) {
                return p != b.p;
            }
        };
        inline iterator begin() {
            return iterator {buf};
        }
        inline iterator end() {
            return iterator {buf + R - L + 1};
        }
        inline T& operator [] (int x) {
            return buf[x - L];
        }
