        pw1[-i - 1] = 1ll * base1Inv * pw1[-i] % mod1,
        pw2[-i - 1] = 1ll * base2Inv * pw2[-i] % mod2;
    for(int i = 1; i <= n; i++) {
        switch (opt[i]) {
