            sum[i] = sum[i - 1];
            break;
        }
        case '+': {
            pos[i] = pos[i - 1];
            sum[i] = sum[i - 1];
            sum[i].first = (sum[i].first + pw1[pos[i]]) % mod1;
            sum[i].second = (sum[i].second + pw2[pos[i]]) % mod2;
            break;
        }
        case '-': {
            pos[i] = pos[i - 1];
            sum[i] = sum[i - 1];
